import React, { useState, useEffect } from 'react';
import { 
  getSimpleFixtures,
  saveBroadcaster,
  SIMPLE_BROADCASTERS,
  type SimpleFixture 
} from '../services/supabase-simple';

const AdminPage: React.FC = () => {
  const [fixtures, setFixtures] = useState<SimpleFixture[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [message, setMessage] = useState<{ text: string; type: 'success' | 'error' } | null>(null);
  const [pendingChanges, setPendingChanges] = useState<Map<number, number | null>>(new Map());
  const [savingFixtures, setSavingFixtures] = useState<Set<number>>(new Set());

  useEffect(() => {
    loadFixtures();
  }, []);

  const loadFixtures = async () => {
    try {
      setLoading(true);
      setError(null);
      const fixturesData = await getSimpleFixtures();
      setFixtures(fixturesData);
    } catch (err) {
      console.error('Failed to load fixtures:', err);
      setError('Failed to load fixtures. Please try again later.');
    } finally {
      setLoading(false);
    }
  };

  const showMessage = (text: string, type: 'success' | 'error' = 'success') => {
    setMessage({ text, type });
    setTimeout(() => setMessage(null), 5000);
  };

  const handleProviderChange = (fixtureId: number, providerId: string) => {
    const newChanges = new Map(pendingChanges);
    const fixture = fixtures.find(f => f.id === fixtureId);
    const currentProviderId = SIMPLE_BROADCASTERS.find(b => b.name === fixture?.broadcaster)?.id;

    const selectedProviderId = providerId === '' ? null : parseInt(providerId);

    if (selectedProviderId !== currentProviderId) {
      newChanges.set(fixtureId, selectedProviderId);
    } else {
      newChanges.delete(fixtureId);
    }

    setPendingChanges(newChanges);
  };

  const handleSaveBroadcast = async (fixtureId: number) => {
    const providerId = pendingChanges.get(fixtureId);
    if (providerId === undefined) return;

    setSavingFixtures(prev => {
      const newSet = new Set(prev);
      newSet.add(fixtureId);
      return newSet;
    });

    try {
      await saveBroadcaster(fixtureId, providerId);

      // Update local fixture data
      setFixtures(prev => prev.map(f => {
        if (f.id === fixtureId) {
          const broadcaster = providerId ? SIMPLE_BROADCASTERS.find(b => b.id === providerId)?.name : undefined;
          return { ...f, broadcaster };
        }
        return f;
      }));

      // Remove from pending changes
      setPendingChanges(prev => {
        const newChanges = new Map(prev);
        newChanges.delete(fixtureId);
        return newChanges;
      });

      const broadcasterName = providerId ? SIMPLE_BROADCASTERS.find(b => b.id === providerId)?.name || 'Unknown' : 'TBD';
      showMessage(`Successfully updated broadcaster to ${broadcasterName}`, 'success');

    } catch (err) {
      console.error('Failed to save broadcast:', err);
      showMessage(`Failed to save broadcaster for fixture ${fixtureId}`, 'error');
    } finally {
      setSavingFixtures(prev => {
        const newSet = new Set(prev);
        newSet.delete(fixtureId);
        return newSet;
      });
    }
  };

  const formatDateTime = (utcKickoff: string) => {
    const date = new Date(utcKickoff);
    return date.toLocaleDateString('en-GB', {
      weekday: 'short',
      month: 'short',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit',
      timeZone: 'Europe/London'
    });
  };

  if (loading) {
    return (
      <div className="admin-page">
        <header className="admin-header">
          <div className="container">
            <h1>Broadcast Admin - Premier League TV Schedule</h1>
            <p>Manage TV broadcaster assignments for upcoming fixtures</p>
          </div>
        </header>

        <main className="container">
          <div className="loading">Loading fixtures...</div>
        </main>
      </div>
    );
  }

  if (error) {
    return (
      <div className="admin-page">
        <header className="admin-header">
          <div className="container">
            <h1>Broadcast Admin - Premier League TV Schedule</h1>
            <p>Manage TV broadcaster assignments for upcoming fixtures</p>
          </div>
        </header>

        <main className="container">
          <div className="error">{error}</div>
          <button onClick={loadFixtures} className="save-btn">Retry</button>
        </main>
      </div>
    );
  }

  return (
    <div className="admin-page">
      <header className="admin-header">
        <div className="container">
          <h1>Broadcast Admin - Premier League TV Schedule</h1>
          <p>Manage TV broadcaster assignments for upcoming fixtures</p>
        </div>
      </header>

      <main className="container">
        {message && (
          <div className={`message ${message.type}`}>
            {message.text}
          </div>
        )}

        {/* Stats Cards */}
        <div className="stats-grid">
          <div className="stats-card">
            <div className="stats-number total">{stats.total}</div>
            <div className="stats-label">Total</div>
          </div>
          <div className="stats-card">
            <div className="stats-number confirmed">{stats.confirmed}</div>
            <div className="stats-label">Confirmed</div>
          </div>
          <div className="stats-card">
            <div className="stats-number blackout">{stats.blackout}</div>
            <div className="stats-label">Blackout</div>
          </div>
          <div className="stats-card">
            <div className="stats-number pending">{stats.pending}</div>
            <div className="stats-label">TBD</div>
          </div>
        </div>

        {/* Filter Bar */}
        <div className="filter-bar">
          <select 
            value={monthFilter} 
            onChange={(e) => setMonthFilter(e.target.value)}
            className="filter-select"
          >
            <option value="">All months</option>
            {getMonthOptions().map(month => (
              <option key={month} value={month}>
                {new Date(month + '-01').toLocaleDateString('en-GB', { month: 'long', year: 'numeric' })}
              </option>
            ))}
          </select>

          <select 
            value={matchweekFilter} 
            onChange={(e) => setMatchweekFilter(e.target.value)}
            className="filter-select"
          >
            <option value="">All matchweeks</option>
            {getMatchweekOptions().map(mw => (
              <option key={mw} value={mw}>Matchweek {mw}</option>
            ))}
          </select>

          <select 
            value={statusFilter} 
            onChange={(e) => setStatusFilter(e.target.value as FilterStatus)}
            className="filter-select"
          >
            <option value="">All fixtures</option>
            <option value="confirmed">Confirmed broadcaster</option>
            <option value="blackout">Confirmed blackout</option>
            <option value="tbd">TBD (awaiting announcement)</option>
          </select>

          <button onClick={loadFixtures} className="save-btn">
            ðŸ”„ Refresh
          </button>

          {pendingChanges.size > 0 && (
            <button 
              onClick={saveAllChanges} 
              className="save-btn save-all-btn"
            >
              ðŸ’¾ Save All Changes ({pendingChanges.size})
            </button>
          )}
        </div>

        {/* Fixtures Table */}
        <div className="fixtures-table">
          <table>
            <thead>
              <tr>
                <th>Date</th>
                <th>Match</th>
                <th>MW</th>
                <th>Broadcaster</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {filteredFixtures.map(fixture => {
                const status = getFixtureStatus(fixture);
                const hasPendingChange = pendingChanges.has(fixture.id);
                const isSaving = savingFixtures.has(fixture.id);
                const currentProviderId = fixture.broadcast?.provider_id?.toString() || '';
                const pendingProviderId = pendingChanges.get(fixture.id) || currentProviderId;

                return (
                  <tr key={fixture.id} className="fixture-row">
                    <td className="match-time">
                      {formatDateTime(fixture.kickoff_utc)}
                    </td>
                    <td className="match-info">
                      <div>{fixture.home.name} vs {fixture.away.name}</div>
                    </td>
                    <td>{fixture.matchweek || '-'}</td>
                    <td>
                      <div className="broadcast-status">
                        <span className={`status-indicator ${status.class}`}></span>
                        {fixture.broadcast?.provider_display_name || 'TBD'}
                      </div>
                    </td>
                    <td>
                      <div className="broadcast-editor">
                        <select
                          value={pendingProviderId}
                          onChange={(e) => handleProviderChange(fixture.id, e.target.value)}
                          className="broadcast-select"
                          disabled={isSaving}
                        >
                          <option value="">-- Select broadcaster --</option>
                          {BROADCASTERS.map(broadcaster => (
                            <option key={broadcaster.id} value={broadcaster.id}>
                              {broadcaster.name}
                            </option>
                          ))}
                        </select>

                        {hasPendingChange && (
                          <button
                            onClick={() => handleSaveBroadcast(fixture.id)}
                            disabled={isSaving}
                            className="save-btn save-btn-small"
                          >
                            {isSaving ? 'Saving...' : 'Save'}
                          </button>
                        )}
                      </div>
                    </td>
                  </tr>
                );
              })}
            </tbody>
          </table>
        </div>

        {filteredFixtures.length === 0 && (
          <div className="no-fixtures">
            <p>No fixtures match the current filters.</p>
          </div>
        )}
      </main>
    </div>
  );
};

export default AdminPage;
