import React, { useState, useEffect } from 'react';
import { getSimpleFixtures, type SimpleFixture } from '../services/supabase-simple';

interface MatchDay {
  date: string;
  displayDate: string;
  fixtures: SimpleFixture[];
  isToday: boolean;
  isUpcoming: boolean;
}

const HomePage: React.FC = () => {
  const [matchDay, setMatchDay] = useState<MatchDay | null>(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    loadMatchDay();
  }, []);

  const loadMatchDay = async () => {
    try {
      setLoading(true);
      setError(null);

      const fixturesData = await getSimpleFixtures();
      const currentMatchDay = getCurrentOrUpcomingMatchDay(fixturesData);
      setMatchDay(currentMatchDay);
    } catch (err) {
      console.error('Failed to load fixtures:', err);
      setError('Failed to load fixtures. Please try again later.');
    } finally {
      setLoading(false);
    }
  };

  const getCurrentOrUpcomingMatchDay = (fixtures: SimpleFixture[]): MatchDay | null => {
    if (!fixtures.length) return null;

    const now = new Date();
    const today = new Date(now.getFullYear(), now.getMonth(), now.getDate());

    // Group fixtures by date
    const fixturesByDate = new Map<string, SimpleFixture[]>();

    fixtures.forEach(fixture => {
      const fixtureDate = new Date(fixture.kickoff_utc);
      const dateKey = fixtureDate.toISOString().split('T')[0]; // YYYY-MM-DD

      if (!fixturesByDate.has(dateKey)) {
        fixturesByDate.set(dateKey, []);
      }
      fixturesByDate.get(dateKey)!.push(fixture);
    });

    // Sort dates and find current/next match day
    const sortedDates = Array.from(fixturesByDate.keys()).sort();

    // First, check if there are matches today
    const todayKey = today.toISOString().split('T')[0];
    if (fixturesByDate.has(todayKey)) {
      const todayFixtures = fixturesByDate.get(todayKey)!;
      return {
        date: todayKey,
        displayDate: 'Today',
        fixtures: todayFixtures.sort((a, b) => new Date(a.kickoff_utc).getTime() - new Date(b.kickoff_utc).getTime()),
        isToday: true,
        isUpcoming: false
      };
    }

    // Otherwise, find the next upcoming match day
    for (const dateKey of sortedDates) {
      const matchDate = new Date(dateKey);
      if (matchDate >= today) {
        const fixtures = fixturesByDate.get(dateKey)!;
        const displayDate = matchDate.toLocaleDateString('en-GB', {
          weekday: 'long',
          day: 'numeric',
          month: 'long'
        });

        return {
          date: dateKey,
          displayDate,
          fixtures: fixtures.sort((a, b) => new Date(a.kickoff_utc).getTime() - new Date(b.kickoff_utc).getTime()),
          isToday: false,
          isUpcoming: true
        };
      }
    }

    return null;
  };

  if (loading) {
    return (
      <div className="home-page">
        <header>
          <h1>Premier League TV Schedule</h1>
          <p>Find which broadcaster shows every match: Sky Sports, TNT Sports</p>
        </header>

        <main>
          <div className="wrap">
            <div className="loading">Loading fixtures...</div>
          </div>
        </main>
      </div>
    );
  }

  if (error) {
    return (
      <div className="home-page">
        <header>
          <h1>Premier League TV Schedule</h1>
          <p>Find which broadcaster shows every match: Sky Sports, TNT Sports</p>
        </header>

        <main>
          <div className="wrap">
            <div className="error">{error}</div>
            <button onClick={loadFixtures}>Retry</button>
          </div>
        </main>
      </div>
    );
  }

  return (
    <div className="home-page">
      <header>
        <div>
          <h1 style={{ margin: 0 }}>Premier League TV Schedule UK</h1>
          <p style={{ margin: 0, color: 'var(--color-muted)' }}>Find which broadcaster shows every match: Sky Sports, TNT Sports, BBC</p>
        </div>
        <nav style={{ display: 'flex', gap: '12px' }}>
          <a href="/clubs" style={{ color: '#6366f1', textDecoration: 'underline', fontSize: '0.9rem' }}>Clubs</a>
          <a href="/about" style={{ color: '#6366f1', textDecoration: 'underline', fontSize: '0.9rem' }}>About</a>
          <a href="/admin" style={{ color: '#6366f1', textDecoration: 'underline', fontSize: '0.9rem' }}>Admin</a>
        </nav>
      </header>

      <main>
        <div className="wrap">
          {fixtures.length === 0 ? (
            <div className="no-fixtures">
              <p>No upcoming fixtures found.</p>
            </div>
          ) : (
            <div className="fixtures-list">
              <h2>Upcoming Fixtures ({Math.min(50, fixtures.length)} of {fixtures.length}) 
                <a href="/admin" style={{ marginLeft: '15px', color: '#6366f1', textDecoration: 'underline', fontSize: '0.8rem' }}>
                  ðŸ”§ Admin
                </a>
              </h2>
              {fixtures.slice(0, 50).map(fixture => (
                <div key={fixture.id} className="fixture-card">
                  <div className="fixture-datetime">
                    {new Date(fixture.kickoff_utc).toLocaleDateString('en-GB', {
                      weekday: 'short',
                      month: 'short', 
                      day: 'numeric',
                      hour: '2-digit',
                      minute: '2-digit',
                      timeZone: 'Europe/London'
                    })}
                  </div>
                  <div className="fixture-teams">
                    <div className="team">
                      <span className="team-name">{fixture.home_team}</span>
                    </div>
                    <div className="vs">vs</div>
                    <div className="team away-team">
                      <span className="team-name">{fixture.away_team}</span>
                    </div>
                  </div>
                  <div className="broadcaster-info">
                    {fixture.broadcaster ? (
                      <span className="provider confirmed">{fixture.broadcaster}</span>
                    ) : (
                      <span className="tbd-text">TBD</span>
                    )}
                  </div>
                  <div style={{ marginTop: '12px', textAlign: 'right' }}>
                    <a 
                      href={`/matches/${fixture.id}`} 
                      style={{ 
                        color: '#6366f1', 
                        textDecoration: 'underline', 
                        fontSize: '0.9rem',
                        fontWeight: '500'
                      }}
                    >
                      Details â†’
                    </a>
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>
      </main>
    </div>
  );
};

export default HomePage;
